import React, { useState } from 'react';
import './App.css';
import ColorThief from 'colorthief';

function App() {
  const [image, setImage] = useState(null);
  const [dominantColor, setDominantColor] = useState('white');

  const onImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      let img = event.target.files[0];
      setImage(URL.createObjectURL(img));
    }
  };

  const onLoad = () => {
    const colorThief = new ColorThief();
    const img = document.querySelector('#uploadedImage');
    setDominantColor(`rgb(${colorThief.getColor(img).join(',')})`);
  };

  return (
      <div className="App" style={{ backgroundColor: dominantColor }}>
        <header className="App-header">
          <input type="file" onChange={onImageChange} accept="image/*" />
          {image && <img id="uploadedImage" src={image} onLoad={onLoad} alt="Uploaded" />}
        </header>
      </div>
  );
}

export default App;
